!(function(e) {
    "use strict";
    e('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function() {
            if (
                location.pathname.replace(/^\//, "") ==
                this.pathname.replace(/^\//, "") &&
                location.hostname == this.hostname
            ) {
                var a = e(this.hash);
                if ((a = a.length ? a : e("[name=" + this.hash.slice(1) + "]")).length)
                    return (
                        e("html, body").animate({ scrollTop: a.offset().top - 48 },
                            1e3,
                            "easeInOutExpo"
                        ), !1
                    );
            }
        }),
        e(".js-scroll-trigger").click(function() {
            e(".navbar-collapse").collapse("hide");
        }),
        e("body").scrollspy({ target: "#mainNav", offset: 54 });

    function a() {
        100 < e("#mainNav").offset().top ?
            e("#mainNav").addClass("navbar-shrink") :
            e("#mainNav").removeClass("navbar-shrink");
    }
    a(), e(window).scroll(a);

    var typed = new Typed("#displaytext", {
        strings: ["Apps", "Products", "Solutions", "Platforms", "Frameworks"],
        typeSpeed: 60,
        backSpeed: 60,
        backDelay: 3000,
        loop: true,
    });

    (function() {
        var rotator = document.getElementById("techimg"), //get the element
            dir = "/img/techs/", //images folder
            delayInSeconds = 4000, //delay in seconds
            num = 1, //start number
            len = 8; //limit
        setInterval(function() {
            //interval changer
            rotator.src = dir + "0 (" + num + ")" + ".png"; //change picture
            num = num === len ? 1 : ++num; //reset if last image reached
        }, delayInSeconds);
    })();
})(jQuery);